$(document).ready(function(){$("#imageInput").on("change",function(e){var t;e.target.files&&e.target.files[0]&&((t=new FileReader).onload=function(e){$("#imagePreview").attr("src",e.target.result),$("#imagePreviewBox").css("display","block")},t.readAsDataURL(e.target.files[0]))})}),$(document).ready(function(){$("#toggleButton").click(function(e){e.preventDefault(),$("#popup-update").slideToggle(300)})});class ToggleFriendship{constructor(e){this.toggler=e,this.toggleFriend()}toggleFriend(){$(this.toggler).click(function(e){e.preventDefault();let t=this;$.ajax({type:"POST",url:$(t).attr("href")}).done(function(e){1==e.data.friends?$(t).html("Remove Friend"):$(t).html("Add Friend")}).fail(function(e){console.log("error in completing the request")})})}}export{ToggleFriendship};